import React, { Component } from 'react'
import NewsItem from './NewsItem'
import Spinner from './Spinner'
export default class News extends Component {
  constructor() {
    super()
    this.state = {
      articles: [],
      loading: false,
      page: 1,
      totalArticles: 0
    }
  }
  async componentDidMount() {
    let url = `https://newsapi.org/v2/top-headlines?country=in&category=business&apiKey=a643bfc7ba45429686c7769b0f99c749&page=1&pageSize=${this.props.pageSize}`;
    let data = await fetch(url);
    let parsedData = await data.json();
    // console.log(parsedData);
    this.setState(
      {
        articles: parsedData.articles,
        totalArticles: parsedData.totalResults
      }
    )
  }
  handleNextClick = async () => {
    if (this.state.page + 1 > Math.ceil(this.state.totalArticles / 20)) {

    }
    else {
      let url = `https://newsapi.org/v2/top-headlines?country=in&category=business&apiKey=a643bfc7ba45429686c7769b0f99c749&page=${this.state.page + 1}&pageSize=${this.props.pageSize}`;
      let data = await fetch(url);
      let parsedData = await data.json();
      // console.log(parsedData);
      this.setState(
        {
          page: this.state.page + 1,
          articles: parsedData.articles
        }
      )
    }
  }
  handlePreviousClick = async () => {
    let url = `https://newsapi.org/v2/top-headlines?country=in&category=business&apiKey=a643bfc7ba45429686c7769b0f99c749&page=${this.state.page - 1}&pageSize=${this.props.pageSize}`;
    let data = await fetch(url);
    let parsedData = await data.json();
    // console.log(parsedData);
    this.setState(
      {
        page: this.state.page - 1,
        articles: parsedData.articles
      }
    )
  }
  render() {
    return (
      <div className="container my-3">
        <h2 className='text-center'>NewsMonkey|Top-Headlines</h2>
        <Spinner/>
        <div className="row">
          {this.state.articles.map((element) => {
            return <div className="col-md-4" key={element.url}>
              <NewsItem title={element.title ? element.title.slice(0, 45) : ""} description={element.description ? element.description.slice(0, 88) : ""} imageUrl={element.urlToImage ? element.urlToImage : "https://img.etimg.com/thumb/msid-91711122,width-1070,height-580,imgsize-2155739,overlay-ettech/photo.jpg"} url={element.url} />
            </div>
          })}
        </div>
        <div className="d-flex justify-content-between">
          <button disabled={this.state.page <= 1} type="button" className="btn btn-dark" onClick={this.handlePreviousClick}>&#8592;previous</button>
          <button disabled={this.state.page + 1 > Math.ceil(this.state.totalArticles / 20)}type="button" className="btn btn-dark" onClick={this.handleNextClick}>Next&#8594;</button>
        </div>
      </div>
    )
  }
}
